import React from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

import PauseButton from './PauseButton';
import PlayButton from './PlayButton';
import SettingsButton from './SettingsButton';
import { useState, useEffect } from 'react';


const Timer = ({handleClickSettings, timerLength, workBreak}) => {

  const [isPaused, setIsPaused] = useState(true);
  const [secondsLeft, setSecondsLeft] = useState(timerLength * 60);
  const [mode, setMode ] = useState('work');


  useEffect(() => {

    function switchMode() {
      const nextMode = mode === 'work' ? 'break' : 'work';
      const nextSeconds = (nextMode === 'work' ? timerLength : workBreak) * 60;

      setMode(nextMode);

      setSecondsLeft(nextSeconds);
    }

    setSecondsLeft(secondsLeft)

    if(isPaused) {
      const interval = setInterval(() => {
        setSecondsLeft(secondsLeft => secondsLeft - 1)
      }, 1000)
    
      if(secondsLeft === 0) {
        switchMode()
        clearInterval(interval)
        setIsPaused(false)
      }

      return () => clearInterval(interval)
    }
    
  }, [isPaused, secondsLeft, mode]);


  const formatTime = (seconds) => {
    return(`${Math.floor(seconds / 60)}:${
            (seconds % 60 > 9)
              ? seconds % 60
              : '0' + seconds % 60
          }`)
  }

  const percentage = () => {
    if(mode === 'work') {
      return((secondsLeft / (timerLength * 60)) * 100)
    }
    if(mode === 'break') {
      return((secondsLeft / (workBreak * 60)) * 100)
    }
  }
  
    

  return (
    <div>
        <CircularProgressbar value={formatTime} text={`60%`} styles={buildStyles( {
            strokeLinecap: 'round', 
            textColor: '#fff', pathColor:'#77a6b6', trailColor:'#c9ffe2'
        })} />
    <div style={{ margin:'20px'}}>
      {
        isPaused ? 
        <PlayButton />
        :
        <PauseButton />
      }
       
       
        <div style={{ marginTop: '20px'}}>
            <SettingsButton onClick={handleClickSettings}/>
        </div>
    </div>
    </div>
  )
}

export default Timer
