import React from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

import PauseButton from './PauseButton';
import PlayButton from './PlayButton';
import SettingsButton from './SettingsButton';
import { useState, useEffect } from 'react';


const Timer = ({handleClickSettings, timerLength, workBreak}) => {

  const [isPaused, setIsPaused] = useState(true);
  const [secondsLeft, setSecondsLeft] = useState(timerLength * 60);
  const [mode, setMode ] = useState('work');


  useEffect(() => {

    function switchMode() {
      const nextMode = mode === 'work' ? 'break' : 'work';
      // const nextSeconds = (nextMode === 'work' ? timerLength : workBreak) * 60;

      setMode(nextMode);

      // setSecondsLeft(nextSeconds);
    }

    // setSecondsLeft(secondsLeft)

    if(isPaused) {
      const interval = setInterval(() => {
        setSecondsLeft(secondsLeft => secondsLeft - 1)
      }, 1000)
    
      if(secondsLeft === 0) {
        switchMode()
        clearInterval(interval)
        setIsPaused(false)
      }

      return () => clearInterval(interval)
    }
    
  }, [mode, isPaused, secondsLeft, workBreak, timerLength]);


  const totalSeconds = mode === 'work'
    ? timerLength
    : workBreak * 60;
  const percentage = Math.round(secondsLeft / totalSeconds * 100);

  const minutes = Math.floor(secondsLeft / 60);
  let seconds = secondsLeft % 60;
  if(seconds < 10) seconds = '0'+ seconds;

  console.log(minutes)
  console.log(secondsLeft)
  console.log(timerLength)
    

  return (
    <div>
        <CircularProgressbar 
        value={percentage} 
        text={minutes + ':' + seconds} 
        styles={buildStyles( {
            strokeLinecap: 'round', 
            textColor: '#fff', pathColor:'#77a6b6', trailColor:'#c9ffe2'
        })} />
    <div style={{ margin:'20px'}}>
      {
        isPaused ? 
        <PlayButton />
        :
        <PauseButton />
      }
       
       
        <div style={{ marginTop: '20px'}}>
            <SettingsButton onClick={handleClickSettings}/>
        </div>
    </div>
    </div>
  )
}

export default Timer
