import React from "react";
import PauseButton from "./PauseButton";
import PlayButton from "./PlayButton";
import ResetButton from "./ResetButton";
import { useState, useEffect } from "react";

const Timer = () => {
  const [timerLength, setTimerLength] = useState(25);
  const [workBreak, setWorkBreak] = useState(5);
  const [isPaused, setIsPaused] = useState(true);
  const [workTime, setWorkTime] = useState(0);
  const [secondsLeft, setSecondsLeft] = useState(workTime);
  const [mode, setMode] = useState("work");

  useEffect(() => {
    function switchMode() {
      const nextMode = mode === "break" ? "work" : "break";
      const nextSeconds =
        nextMode === "work" ? timerLength * 60 : workBreak * 60;

      setMode(nextMode);
      setSecondsLeft(nextSeconds);
    }
    setSecondsLeft(secondsLeft);

    if (!isPaused) {
      const interval = setInterval(() => {
        setSecondsLeft((secondsLeft) => secondsLeft - 1);
      }, 1000);

      if (secondsLeft === 0) {
        switchMode();
        clearInterval(interval);
        setIsPaused(false);
      }

      return () => clearInterval(interval);
    }
  }, [mode, isPaused, secondsLeft, workBreak, timerLength]);

  const minutes = Math.floor(secondsLeft / 60);
  let seconds = secondsLeft % 60;
  if (seconds < 10) seconds = "0" + seconds;

  const handlePause = () => {
    setIsPaused(!isPaused);
  };

  const handleReset = () => {};

  const handleTimerChange = (e) => {
    setTimerLength(e.target.value);
    setWorkTime(e.target.value);
  };

  const handleBreakChange = (e) => {
    setWorkBreak(e.target.value);
  };

  return (
    <div>
      <div>
        <label>Work minutes: {timerLength}:00</label>
        <input
          type="number"
          name="pomodoro"
          id="pomodoro"
          min="5"
          max="90"
          value={timerLength}
          onChange={handleTimerChange}
        ></input>
        <label>Break minutes: {workBreak}:00</label>
        <input
          type="number"
          name="workBreak"
          id="workBreak"
          min="1"
          max="30"
          value={workBreak}
          onChange={handleBreakChange}
        ></input>
      </div>

      <div>
        {mode === "work" ? <p>Working time!</p> : <p>You're on Break!</p>}
      </div>
      <div className="timer">{minutes + ":" + seconds}</div>
      <div style={{ margin: "20px" }}>
        {isPaused ? (
          <PlayButton onClick={handlePause} />
        ) : (
          <PauseButton onClick={handlePause} />
        )}
        <ResetButton onClick={handleReset} />

        <div style={{ marginTop: "20px" }}></div>
      </div>
    </div>
  );
};

export default Timer;
