import React from "react";
import Animations from "./Animations";
import { useState, useEffect } from "react";
import Controls from "./Controls";
import Range from "./Range";
import Display from "./Display";

const interval = null;

const Timer = () => {
  const [mode, setMode] = useState("work");
  const [timerLength, setTimerLength] = useState(25);
  const [secondsLeft, setSecondsLeft] = useState(timerLength * 60);
  const [isPaused, setIsPaused] = useState(true);

  useEffect(() => {
    return () => clearInterval(interval);
  }, []);

  // function switchMode() {
  //   const nextMode = mode === "work" ? "break" : "work";
  //   const nextSeconds = nextMode === "work" ? timerLength * 60 : workBreak * 60;

  //   setMode(nextMode);
  //   setSecondsLeft(nextSeconds);
  // }

  return (
    <div>
      <Range setSecondsLeft={setSecondsLeft} />
      <div>
        {mode === "work" ? (
          <h2 className="text">Working time!</h2>
        ) : (
          <h2 className="text">You're on Break!</h2>
        )}
      </div>
      <Display secondsLeft={secondsLeft} />
      <Controls
        timerLength={timerLength}
        setTimerLength={setTimerLength}
        secondsLeft={setSecondsLeft}
        setSecondsLeft={setSecondsLeft}
        isPaused={isPaused}
        setIsPaused={setIsPaused}
        setMode={setMode}
      />
      <Animations />
    </div>
  );
};

export default Timer;
